Node.OnInit = function() {
  // Create a variable to hold global queries
  Noodl.Variables['Active Media Queries'] = []

  this.createdQueries = []
  this.queries = {}
  this.listeners = {}

  this.createListener = (queryObject) => {
    const qId = queryObject.name

    this.listeners[qId] = query => {
      if (query.matches) {
        console.log(qId)
        Noodl.Variables['Active Media Queries'].push(qId)
      } else {
        Noodl.Variables['Active Media Queries'] = Noodl.Variables['Active Media Queries'].filter(q => q !== qId)
      }
    }

    this.createdQueries.push(qId)
    this.queries[qId] = window.matchMedia(queryObject.query)
    this.listeners[qId](this.queries[qId])
    this.queries[qId].addEventListener('change', this.listeners[qId])
  }

  queryObjects.forEach((queryObject) => {
    this.createListener(queryObject)
  })    
}

Node.OnDestroy = function() {
    if(this.createdQueries.length) {
      this.createdQueries.forEach(qId => {
        this.queries[qId].removeEventListener('change', this.listeners[qId])
        this.queries[qId] = undefined
        this.listeners[qId] = undefined
        this.createdQueries = this.createdQueries.filter(query => query !== qId)
      })
    }
}
